hero-section-wilunch.html.css:
.hero-section {
  height: 100vh;
  background-image: url("hero-img-pizza.webp");
  background-size: 100% 100%;
}

.hero-section>div {
  color: white;
  backdrop-filter: blur(5px);
}

.custom-button {
  background-color: #13ad7b;
  border: none;
  color: white;
  padding: 15px 32px;
  text-align: center;
  box-shadow: 0 8px 16px 0 rgba(65, 63, 63, 0.2), 0 6px 20px 0 rgba(73, 73, 73, 0.19);
}

hero-section-digital.html.css:
.hero {
  min-height: 100vh;
  background-image: linear-gradient(#6ED5C3, white);
}

.hero-banner {
  background-image: url("hero4.png");
  background-size: 100%;
  background-repeat: no-repeat;
  background-position: center;
}

.hero-text h1 {
  font-size: 50px;
  font-weight: bold;
}

.cta-btn {
  width: 160px;
}

.cta-btn a {
  text-decoration: none;
}

.cta-btn:hover {
  background-color: black;
}

navbar-digital.html.css:
nav a {
  color: white;
  box-sizing: border-box;
  text-decoration: none;
  padding: 15px 0;
  border-bottom: 2px solid transparent;
  transition: 0.3s;
  align-self: stretch;
}

.navbar {
  height: 59px;
  z-index: 1;
}

.navbar-nav {
  overflow-y: hidden;
}

.site-title {
  font-size: 25px;
  font-weight: 200;
}

.site-title span {
  font-weight: bold;
}

@media screen and (min-width: 576px) {
  .navbar-nav .link:hover {
    border-bottom: 2px solid white;
    transition: 0.3s;
  }

  .navbar-nav .link {
    margin-right: 25px;
  }
}

@media screen and (max-width: 576px) {
  nav>div {
    padding-bottom: 8px;
  }

  .navbar-nav {
    text-align: center;
  }

  .navbar-nav a {
    color: black;
    background-color: antiquewhite;
    margin: 4px 0;
    border-radius: 4px;
  }

  .navbar-nav a:hover {
    color: white;
    background-color: black;
  }

  .cart-items-count {
    color: white;
  }

  .cart-items-count:hover {
    background-color: white;
  }
}

.cart-items-count {
  border-radius: 50%;
  background-color: black;
  font-size: 10px;
  position: relative;
  top: 9px;
  left: -4px;
  padding: 2px 6px;
}

navbar-wilunch.html.css:
@media screen and (max-width: 576px) {
  nav {
    flex-direction: column;

  }

  nav .nav-links {
    flex-direction: column;
    width: 100%;
    text-align: center;
    border-top: 1px solid white;
  }

  nav .nav-links a {
    padding: 10px;
    margin: 0;
  }

  .menu-btn {
    visibility: visible;
  }
}

@media screen and (min-width: 576px) {
  .menu-btn {
    visibility: hidden;
  }
}

nav {
  align-items: stretch;
  background-color: #334858;
  color: white;
}

.site-title {
  text-decoration: none;
}

.lunch {
  color: #13ad7b;
}

nav a.link {
  padding: 16px;
  margin: 0 8px;
  text-decoration: none;
  color: white;
}

nav a.link:hover {
  background-color: #13ad7b;
}

.menu-btn {
  top: 0;
  right: 20px;
  cursor: pointer;
}

navbar-portfolio.html.css:
@media screen and (max-width: 576px) {
  nav {
    height: 40px;
  }

  .nav-links {
    flex-direction: column;
    position: absolute;
    top: 100%;
    width: 100%;
    text-align: center;
    overflow-y: hidden;
  }

  .nav-links a {
    margin: 4px;
    padding: 4px;
    background-color: #3700b3;
  }

  nav i {
    color: #e8e8e8;
  }

  .drop-btn {
    display: none;
  }

  .menu-btn {
    cursor: pointer;
  }
}

@media screen and (min-width: 576px) {
  nav {
    padding: 6px;
  }

  .nav-links {
    width: 70%;
    justify-content: space-between;
    align-items: center;
  }

  nav a:hover {
    color: #3700b3;
    transition: 0.2s;
  }
}

nav {
  background-color: #121212;
}

nav a {
  text-decoration: none;
  color: #e8e8e8;
  font-size: 18px;
  font-weight: bold;
  transition: 0.2s;
}

.mydropdown {
  min-width: 160px;
  text-align: start;
  padding: 12px;
  background-color: #303030;
  border-radius: 5px;
  transition: 0.1s;
}

.mydropdown a:hover {
  color: #3700b3;
  transition: 0.1s;
}

.mydropdown a {
  font-weight: 200;
}
